package ObjectClass7;/** * Two object are not equal always */public class Circle {    private double radius; /*Implicitly 0.0*/    public Circle(){        radius = 1.0;    }    public Circle(double radius){        this(); /*Circle()*/        this.radius = radius;    }    public void setRadius(double radius){        this.radius = radius;    }    public double getAreaOfCircle(){        return Math.pow(radius, 2)*Math.PI;    }    public static void main(String[] args) {        Circle circle1 = new Circle(); /*Default radius = 1.0*/        System.out.println(circle1.getAreaOfCircle());        circle1.setRadius(2);        System.out.println(circle1.getAreaOfCircle());        Circle circle2 = new Circle(2);        System.out.println(circle2.getAreaOfCircle());        /*This is call anonymous object, no name*/        System.out.println(new Circle(2).getAreaOfCircle());    }}