package ObjectClass7;/** * GUI is the best components for learn OOP concept */import javax.swing.*;public class FrameTest {    public static void main(String[] args) {        JFrame jFrame1 = new JFrame();        jFrame1.setTitle("Window 1");        jFrame1.setSize(300, 200);        jFrame1.setLocation(850, 100);        jFrame1.setLayout(null);        jFrame1.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        jFrame1.setVisible(true);        JFrame jFrame2 = new JFrame();        jFrame2.setTitle("Window 2");        jFrame2.setSize(300, 200);        jFrame2.setLocation(850, 300);        jFrame2.setLayout(null);        jFrame2.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        jFrame2.setVisible(true);    }}