package ObjectClass7;public class ObjectPassInMethod {    public static void showCircleAreas(CircleEncapsulation circleEncapsulation, int times){        System.out.println("Radius\t\tArea");        for(int i = 0; i < times; i++){            System.out.println(circleEncapsulation.getRadius()+"\t\t"+circleEncapsulation.getAreaOfCircle());            circleEncapsulation.setRadius(circleEncapsulation.getRadius()+1);        }    }    public static void main(String[] args) {       CircleEncapsulation circleEncapsulation = new CircleEncapsulation(1);       showCircleAreas(circleEncapsulation, 5);    }}