package ObjectClass7;/** * Seed based random number using Random class. * Same sequence random number. * Application: Software testing and many other application */import java.util.Random;public class RandomClass {    public static void main(String[] args) {        Random random1 = new Random(1);        for(int i = 0; i < 10; i++){            System.out.print(random1.nextInt(1, 10)+" ");        }        System.out.println();        Random random2 = new Random(1);        for(int i = 0; i < 10; i++){            System.out.print(random2.nextInt(1,10)+" ");        }        System.out.println();    }}