package ObjectClass7;/** * toString() method is heavy flexible for programmer */class Human{ /*Implicitly extends Object*/    private String name;    private int age;    private String status;    public Human(){        name = null;        age = 0;        status = null;    }    public Human(String name){        this();        this.name = name;    }    public Human(String name, int age){        this(name);        this.age = age;    }    public Human(String name, int age, String status){        this(name, age);        this.status = status;    }    public void setName(String name) {        this.name = name;    }    public void setAge(int age) {        this.age = age;    }    public void setStatus(String status) {        this.status = status;    }    public int getAge() {        return age;    }    public String getName() {        return name;    }    public String getStatus() {        return status;    }    @Override    public String toString(){        return (name+" "+age+" "+status);    }}public class ToStringMethod {    public static void main(String[] args) {        Human human1 = new Human("Yousuf", 27, "Programmer");        System.out.println(human1.toString());        Human human2 = new Human("Yousuf", 27, "Programmer");        System.out.println(human2);        System.out.println(human1 == human2); /*Reference false*/        System.out.println(human1.equals(human2)); /*Contains also false whereas both values same*/    }}