package ObjectClass7;/*** * This program has problem and that is any one window do exit then program terminate */import javax.swing.*;class Frame1 extends JFrame{    Frame1(){        this.setTitle("Window 1");        this.setSize(300, 200);        this.setLocation(850, 100);        this.setLayout(null);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.setVisible(true);    }}class Frame2 extends JFrame{    Frame2(){        this.setTitle("Window 2");        this.setSize(300, 200);        this.setLocation(850, 300);        this.setLayout(null);        this.setDefaultCloseOperation(JFrame.EXIT_ON_CLOSE);        this.setVisible(true);    }}public class TwoIndividualFrames {    public static void main(String[] args) {        Frame1 frame1 = new Frame1();        Frame2 frame2 = new Frame2();    }}